import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path, { dirname } from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";
import { fileURLToPath } from "url";
import glsl from "vite-plugin-glsl";

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    glsl(), // Add GLSL shader support
  ],
  resolve: {
    alias: {
      "@": path.resolve(__dirname, "client", "src"),
      "@shared": path.resolve(__dirname, "shared"),
    },
  },
  root: path.resolve(__dirname, "client"),
  build: {
    outDir: path.resolve(__dirname, "dist/public"),
    emptyOutDir: true,
  },
  // Add support for large models and audio files
  assetsInclude: ["**/*.gltf", "**/*.glb", "**/*.mp3", "**/*.ogg", "**/*.wav"],
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  // Try to start the Telegram bot (will be disabled if token is missing)
  try {
    await import("./telegram-bot");
  } catch (error) {
    console.log("Telegram bot not started - this is normal if no token is provided");
  }

  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on port 5000
  // this serves both the API and the client
  const port = 5000;
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import crypto from "crypto";
import { PublicKey } from '@solana/web3.js';

function hashPin(pin: string): string {
  return crypto.createHash('sha256').update(pin).digest('hex');
}

function isValidSolanaAddress(address: string): boolean {
  try {
    // Check if it's a valid base58 Solana public key
    new PublicKey(address);
    return true;
  } catch (error) {
    return false;
  }
}

// Helper function to check achievement criteria
async function checkAndAwardAchievements(userId: number): Promise<void> {
  try {
    const user = await storage.getUser(userId);
    if (!user) return;

    const existingAchievements = await storage.getUserAchievements(userId);
    const achievedTypes = existingAchievements.map(a => a.achievementType);

    // Define achievement criteria
    const achievements = [
      {
        type: "FIRST_GAME",
        name: "Getting Started",
        points: 10,
        criteria: () => (user.gamesPlayed || 0) >= 1
      },
      {
        type: "COMPLETE_5_LEVELS", 
        name: "Level Master",
        points: 50,
        criteria: () => (user.levelsCompleted || 0) >= 5
      },
      {
        type: "SCORE_10K",
        name: "High Scorer", 
        points: 100,
        criteria: () => (user.totalScore || 0) >= 10000
      },
      {
        type: "PLAY_10_GAMES",
        name: "Regular Player",
        points: 75,
        criteria: () => (user.gamesPlayed || 0) >= 10
      },
      {
        type: "REACH_LEVEL_5",
        name: "Progress Master",
        points: 50,
        criteria: () => (user.highestLevel || 1) >= 5
      }
    ];

    // Award new achievements
    for (const achievement of achievements) {
      if (!achievedTypes.includes(achievement.type) && achievement.criteria()) {
        await storage.addAchievement({
          userId,
          achievementType: achievement.type,
          achievementName: achievement.name,
          pointsAwarded: achievement.points
        });
      }
    }
  } catch (error) {
    console.error("Error checking achievements:", error);
  }
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Enhanced user registration with wallet verification
  app.post("/api/register", async (req, res) => {
    try {
      const { walletAddress, username, seedPhrase } = req.body;

      if (!walletAddress || !username || !seedPhrase) {
        return res.status(400).json({ error: "Missing required fields" });
      }

      // Validate seed phrase format (4 words)
      const words = seedPhrase.trim().split(/\s+/);
      if (words.length !== 4) {
        return res.status(400).json({ error: "Seed phrase must be exactly 4 words" });
      }

      // Enhanced validation for Solana wallet address
      if (!isValidSolanaAddress(walletAddress)) {
        return res.status(400).json({ error: "Invalid Solana wallet address. Please provide a valid SOL wallet." });
      }

      // Check if wallet address already exists (prevent duplicates)
      const existingUserByWallet = await storage.getUserByWallet(walletAddress);
      if (existingUserByWallet) {
        return res.status(409).json({ error: "Wallet address already registered. Only one account per wallet address is allowed." });
      }

      // Check if username already exists
      const existingUserByUsername = await storage.getUserByUsername(username);
      if (existingUserByUsername) {
        return res.status(409).json({ error: "Username already taken" });
      }

      const hashedSeedPhrase = hashPin(seedPhrase.toLowerCase().trim());

      const user = await storage.createUser({
        walletAddress,
        username,
        pin: hashedSeedPhrase
      });

      // Award first achievement
      await checkAndAwardAchievements(user.id);

      // Return user data (without PIN)
      const safeUser = {
        id: user.id,
        walletAddress: user.walletAddress,
        username: user.username,
        totalScore: user.totalScore,
        levelsCompleted: user.levelsCompleted,
        achievementPoints: user.achievementPoints
      };

      res.json({ success: true, user: safeUser });
    } catch (error) {
      console.error("Registration error:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // Enhanced user login
  app.post("/api/login", async (req, res) => {
    try {
      const { walletAddress, seedPhrase } = req.body;

      if (!walletAddress || !seedPhrase) {
        return res.status(400).json({ error: "Missing wallet address or seed phrase" });
      }

      if (!isValidSolanaAddress(walletAddress)) {
        return res.status(400).json({ error: "Invalid Solana wallet address" });
      }

      const user = await storage.getUserByWallet(walletAddress);
      if (!user) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      const hashedSeedPhrase = hashPin(seedPhrase.toLowerCase().trim());
      if (user.pin !== hashedSeedPhrase) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      // Update last login time
      await storage.updateUserStats(user.id, { lastLoginAt: new Date() });

      // Return enhanced user data
      const safeUser = {
        id: user.id,
        walletAddress: user.walletAddress,
        username: user.username,
        totalScore: user.totalScore,
        levelsCompleted: user.levelsCompleted,
        highestLevel: user.highestLevel,
        gamesPlayed: user.gamesPlayed,
        achievementPoints: user.achievementPoints,
        consecutiveDays: user.consecutiveDays,
        averageScorePerLevel: user.averageScorePerLevel,
        isEligibleForRewards: user.isEligibleForRewards
      };

      res.json({ success: true, user: safeUser });
    } catch (error) {
      console.error("Login error:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // Enhanced score submission with achievement checking
  app.post("/api/submit-score", async (req, res) => {
    try {
      const { walletAddress, level, score, completed, playTimeSeconds } = req.body;

      if (!walletAddress || typeof level !== 'number' || typeof score !== 'number') {
        return res.status(400).json({ error: "Invalid input data" });
      }

      const user = await storage.getUserByWallet(walletAddress);
      if (!user) {
        return res.status(401).json({ error: "User not found" });
      }

      const gameScore = await storage.submitScore({
        userId: user.id,
        level,
        score,
        completed: !!completed,
        playTimeSeconds: playTimeSeconds || 0
      });

      // Check for new achievements
      await checkAndAwardAchievements(user.id);

      // Update leaderboards
      await storage.updateLeaderboards();

      console.log("Score submitted for user:", user.username, "Score:", score, "Level:", level);

      res.json({ success: true, message: "Score submitted successfully" });
    } catch (error) {
      console.error("Score submission error:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // Enhanced leaderboard with multiple types
  app.get("/api/leaderboard", async (req, res) => {
    try {
      const type = req.query.type as 'daily' | 'weekly' | 'all_time' || 'all_time';
      const limit = parseInt(req.query.limit as string) || 50;

      const leaderboard = await storage.getLeaderboard(type, limit);
      res.json(leaderboard);
    } catch (error) {
      console.error("Leaderboard error:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // User profile endpoint
  app.get("/api/profile/:walletAddress", async (req, res) => {
    try {
      const { walletAddress } = req.params;

      const user = await storage.getUserByWallet(walletAddress);
      if (!user) {
        return res.status(404).json({ error: "User not found" });
      }

      const achievements = await storage.getUserAchievements(user.id);
      const recentScores = await storage.getUserScores(user.id);

      const profile = {
        username: user.username,
        walletAddress: user.walletAddress,
        totalScore: user.totalScore,
        levelsCompleted: user.levelsCompleted,
        highestLevel: user.highestLevel,
        gamesPlayed: user.gamesPlayed,
        achievementPoints: user.achievementPoints,
        averageScorePerLevel: user.averageScorePerLevel,
        totalPlayTimeMinutes: user.totalPlayTimeMinutes,
        consecutiveDays: user.consecutiveDays,
        isEligibleForRewards: user.isEligibleForRewards,
        joinedAt: user.createdAt,
        lastActiveAt: user.lastLoginAt,
        achievements: achievements.slice(0, 5), // Recent 5 achievements
        recentScores: recentScores.slice(0, 10) // Recent 10 scores
      };

      res.json(profile);
    } catch (error) {
      console.error("Profile error:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // Admin endpoint for airdrop data export
  app.get("/api/admin/airdrop-data", async (req, res) => {
    try {
      // In production, add authentication middleware here
      const airdropData = await storage.getAirdropData();

      res.setHeader('Content-Type', 'application/json');
      res.setHeader('Content-Disposition', 'attachment; filename="airdrop-eligible-users.json"');
      res.json({
        exportedAt: new Date().toISOString(),
        totalEligibleUsers: airdropData.length,
        users: airdropData
      });
    } catch (error) {
      console.error("Airdrop data export error:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // User achievements endpoint  
  app.get("/api/achievements/:walletAddress", async (req, res) => {
    try {
      const { walletAddress } = req.params;

      const user = await storage.getUserByWallet(walletAddress);
      if (!user) {
        return res.status(404).json({ error: "User not found" });
      }

      const achievements = await storage.getUserAchievements(user.id);
      res.json(achievements);
    } catch (error) {
      console.error("Achievements error:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // Session tracking endpoints
  app.post("/api/session/start", async (req, res) => {
    try {
      const { walletAddress } = req.body;

      const user = await storage.getUserByWallet(walletAddress);
      if (!user) {
        return res.status(404).json({ error: "User not found" });
      }

      const session = await storage.createSession({
        userId: user.id,
        totalPlayTimeMinutes: 0,
        levelsPlayed: 0
      });

      res.json({ success: true, sessionId: session.id });
    } catch (error) {
      console.error("Session start error:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { neon } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-http';
import { eq, desc, sql, and } from 'drizzle-orm';
import { 
  users, 
  gameScores, 
  gameSessions, 
  userAchievements, 
  leaderboards,
  type User, 
  type InsertUser,
  type GameScore,
  type InsertScore,
  type GameSession,
  type InsertSession,
  type UserAchievement,
  type InsertAchievement
} from "../shared/schema";

if (!process.env.DATABASE_URL) {
  throw new Error('DATABASE_URL environment variable is required');
}

const neonClient = neon(process.env.DATABASE_URL!);
export const db = drizzle(neonClient);

export interface IStorage {
  // User management
  getUser(id: number): Promise<User | undefined>;
  getUserByWallet(walletAddress: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: number, updates: Partial<User>): Promise<User | undefined>;
  updateUserStats(userId: number, stats: Partial<User>): Promise<void>;

  // Score management
  submitScore(score: InsertScore): Promise<GameScore>;
  getUserScores(userId: number): Promise<GameScore[]>;
  getTopScores(limit?: number): Promise<any[]>;

  // Session tracking
  createSession(session: InsertSession): Promise<GameSession>;
  endSession(sessionId: number, endTime: Date): Promise<void>;
  getUserSessions(userId: number): Promise<GameSession[]>;

  // Achievement system
  addAchievement(achievement: InsertAchievement): Promise<UserAchievement>;
  getUserAchievements(userId: number): Promise<UserAchievement[]>;

  // Leaderboards
  updateLeaderboards(): Promise<void>;
  getLeaderboard(type: 'daily' | 'weekly' | 'all_time', limit?: number): Promise<any[]>;

  // Admin data export
  getAirdropData(): Promise<any[]>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: number): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.id, id)).limit(1);
    return result[0];
  }

  async getUserByWallet(walletAddress: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.walletAddress, walletAddress)).limit(1);
    return result[0];
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.username, username)).limit(1);
    return result[0];
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const result = await db.insert(users).values({
      ...insertUser,
      lastLoginAt: new Date(),
      totalScore: 0,
      levelsCompleted: 0,
      highestLevel: 1,
      gamesPlayed: 0,
      consecutiveDays: 1,
      totalPlayTimeMinutes: 0,
      averageScorePerLevel: 0,
      achievementPoints: 0,
      isEligibleForRewards: true
    }).returning();
    return result[0];
  }

  async updateUser(id: number, updates: Partial<User>): Promise<User | undefined> {
    const result = await db.update(users).set(updates).where(eq(users.id, id)).returning();
    return result[0];
  }

  async updateUserStats(userId: number, stats: Partial<User>): Promise<void> {
    await db.update(users).set({
      ...stats,
      lastLoginAt: new Date()
    }).where(eq(users.id, userId));
  }

  async submitScore(score: InsertScore): Promise<GameScore> {
    const result = await db.insert(gameScores).values({
      ...score,
      timestamp: new Date()
    }).returning();

    // Update user statistics
    await this.recalculateUserStats(score.userId!);

    return result[0];
  }

  private async recalculateUserStats(userId: number): Promise<void> {
    const userScores = await db.select().from(gameScores).where(eq(gameScores.userId, userId));
    const completedLevels = userScores.filter(s => s.completed).length;
    const totalScore = userScores.reduce((sum, s) => sum + s.score, 0);
    const highestLevel = Math.max(...userScores.map(s => s.level), 1);
    const averageScore = userScores.length > 0 ? totalScore / userScores.length : 0;

    await db.update(users).set({
      totalScore,
      levelsCompleted: completedLevels,
      highestLevel,
      gamesPlayed: userScores.length,
      averageScorePerLevel: averageScore
    }).where(eq(users.id, userId));
  }

  async getUserScores(userId: number): Promise<GameScore[]> {
    return await db.select().from(gameScores).where(eq(gameScores.userId, userId)).orderBy(desc(gameScores.timestamp));
  }

  async getTopScores(limit = 100): Promise<any[]> {
    const topScores = await db
      .select({
        score: gameScores.score,
        level: gameScores.level,
        completed: gameScores.completed,
        timestamp: gameScores.timestamp,
        username: users.username,
        walletAddress: users.walletAddress
      })
      .from(gameScores)
      .innerJoin(users, eq(gameScores.userId, users.id))
      .orderBy(desc(gameScores.score))
      .limit(limit);

    return topScores.map((score, index) => ({
      rank: index + 1,
      username: score.username,
      walletAddress: `${score.walletAddress.slice(0, 4)}...${score.walletAddress.slice(-4)}`,
      score: score.score,
      level: score.level,
      completed: score.completed
    }));
  }

  async createSession(session: InsertSession): Promise<GameSession> {
    const result = await db.insert(gameSessions).values({
      ...session,
      startTime: new Date()
    }).returning();
    return result[0];
  }

  async endSession(sessionId: number, endTime: Date): Promise<void> {
    await db.update(gameSessions).set({ endTime }).where(eq(gameSessions.id, sessionId));
  }

  async getUserSessions(userId: number): Promise<GameSession[]> {
    return await db.select().from(gameSessions).where(eq(gameSessions.userId, userId)).orderBy(desc(gameSessions.startTime));
  }

  async addAchievement(achievement: InsertAchievement): Promise<UserAchievement> {
    const result = await db.insert(userAchievements).values({
      ...achievement,
      unlockedAt: new Date()
    }).returning();

    // Update user achievement points
    await db.update(users).set({
      achievementPoints: sql`${users.achievementPoints} + ${achievement.pointsAwarded}`
    }).where(eq(users.id, achievement.userId!));

    return result[0];
  }

  async getUserAchievements(userId: number): Promise<UserAchievement[]> {
    return await db.select().from(userAchievements).where(eq(userAchievements.userId, userId)).orderBy(desc(userAchievements.unlockedAt));
  }

  async updateLeaderboards(): Promise<void> {
    const today = new Date();
    const weekStart = new Date(today.getFullYear(), today.getMonth(), today.getDate() - today.getDay());

    // Update daily leaderboard
    const dailyScores = await db
      .select({
        userId: gameScores.userId,
        score: sql<number>`MAX(${gameScores.score})`.as('max_score'),
        level: sql<number>`MAX(${gameScores.level})`.as('max_level')
      })
      .from(gameScores)
      .where(sql`DATE(${gameScores.timestamp}) = CURRENT_DATE`)
      .groupBy(gameScores.userId)
      .orderBy(desc(sql`MAX(${gameScores.score})`));

    // Clear existing daily leaderboard for today
    await db.delete(leaderboards).where(and(
      eq(leaderboards.leaderboardType, 'daily'),
      sql`DATE(${leaderboards.periodDate}) = CURRENT_DATE`
    ));

    // Insert new daily rankings
    for (let i = 0; i < dailyScores.length; i++) {
      await db.insert(leaderboards).values({
        userId: dailyScores[i].userId,
        leaderboardType: 'daily',
        score: dailyScores[i].score,
        level: dailyScores[i].level,
        rank: i + 1,
        periodDate: today.toISOString().split('T')[0]
      });
    }
  }

  async getLeaderboard(type: 'daily' | 'weekly' | 'all_time', limit = 50): Promise<any[]> {
    if (type === 'all_time') {
      return await this.getTopScores(limit);
    }

    const result = await db
      .select({
        rank: leaderboards.rank,
        score: leaderboards.score,
        level: leaderboards.level,
        username: users.username,
        walletAddress: users.walletAddress
      })
      .from(leaderboards)
      .innerJoin(users, eq(leaderboards.userId, users.id))
      .where(eq(leaderboards.leaderboardType, type))
      .orderBy(leaderboards.rank)
      .limit(limit);

    return result.map(entry => ({
      rank: entry.rank,
      username: entry.username,
      walletAddress: `${entry.walletAddress.slice(0, 4)}...${entry.walletAddress.slice(-4)}`,
      score: entry.score,
      level: entry.level
    }));
  }

  async getAirdropData(): Promise<any[]> {
    const result = await db
      .select({
        username: users.username,
        walletAddress: users.walletAddress,
        totalScore: users.totalScore,
        levelsCompleted: users.levelsCompleted,
        highestLevel: users.highestLevel,
        gamesPlayed: users.gamesPlayed,
        consecutiveDays: users.consecutiveDays,
        totalPlayTimeMinutes: users.totalPlayTimeMinutes,
        achievementPoints: users.achievementPoints,
        averageScorePerLevel: users.averageScorePerLevel,
        isEligibleForRewards: users.isEligibleForRewards,
        createdAt: users.createdAt,
        lastLoginAt: users.lastLoginAt
      })
      .from(users)
      .where(eq(users.isEligibleForRewards, true))
      .orderBy(desc(users.totalScore));

    return result;
  }
}

export const storage = new DatabaseStorage();

