import React, { useEffect, useState } from 'react';
import { useGameState } from '../lib/stores/useGameState';
import { useAuthStore } from '../lib/stores/useWallet';
import { AuthModal } from './AuthModal';
import { MenuOverlay } from './MenuOverlay';
import { GameOverOverlay } from './GameOverOverlay';
import { Button } from './ui/button';

interface GameUIProps {
    game: Phaser.Game;
    onModeSelect?: (mode: 'drawing' | 'tower-defense') => void;
}

export const GameUI: React.FC<GameUIProps> = ({ game, onModeSelect }) => {
    const { currentScene, level, score, timeLeft, ink } = useGameState();
    const { user, isLoggedIn, logout } = useAuthStore();
    const [showAuthModal, setShowAuthModal] = useState(false);
    const [showSignInWarning, setShowSignInWarning] = useState(true);

    // Hide sign-in warning after 5 seconds
    useEffect(() => {
        if (!isLoggedIn && currentScene === 'game') {
            const timer = setTimeout(() => {
                setShowSignInWarning(false);
            }, 5000);
            return () => clearTimeout(timer);
        }
    }, [isLoggedIn, currentScene]);

    // Reset warning when scene changes
    useEffect(() => {
        if (currentScene === 'game') {
            setShowSignInWarning(true);
        }
    }, [currentScene]);

    const handleStartGame = () => {
        if (game && game.scene) {
            game.scene.start('GameScene');
        }
    };

    const handleNextLevel = () => {
        const gameState = useGameState.getState();
        gameState.setLevel(gameState.level + 1);
        if (game && game.scene) {
            game.scene.start('GameScene');
        }
    };

    const handleRetry = () => {
        const gameState = useGameState.getState();
        gameState.setScore(Math.max(0, gameState.score - 50)); // Small penalty
        gameState.setCapybaraAlive(true); // Reset capybara state
        gameState.setScene('game'); // Ensure scene state is properly set
        if (game && game.scene) {
            game.scene.start('GameScene');
        }
    };

    const handleBackToMenu = () => {
        useGameState.getState().resetGame();
        if (game && game.scene) {
            game.scene.start('MenuScene');
        }
    };

    const formatWalletAddress = (address: string) => {
        return `${address.slice(0, 4)}...${address.slice(-4)}`;
    };

    return (
        <>
            {/* Menu Overlay */}
            {currentScene === 'menu' && (
                <MenuOverlay onModeSelect={onModeSelect || (() => {})} />
            )}

            {/* Game Over Overlay */}
            {currentScene === 'gameover' && (
                <GameOverOverlay 
                    won={useGameState.getState().capybaraAlive}
                    onNextLevel={handleNextLevel}
                    onRetry={handleRetry}
                    onBackToMenu={handleBackToMenu}
                />
            )}

            <div className="absolute inset-0 pointer-events-none">
                {/* Account Button - Always visible */}
                <div className="absolute top-6 right-6 pointer-events-auto z-50">
                    {isLoggedIn ? (
                        <div className="flex gap-3 items-center">
                            <div className="bg-white/95 backdrop-blur text-gray-900 px-4 py-2 rounded-lg shadow-xl border border-gray-200">
                                <div className="flex items-center gap-2">
                                    <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                                    <span className="font-medium">{user?.username}</span>
                                </div>
                                <div className="text-xs text-gray-500 font-mono">{formatWalletAddress(user?.walletAddress || '')}</div>
                            </div>
                            <Button 
                                variant="outline" 
                                size="sm" 
                                onClick={logout}
                                className="bg-white/95 hover:bg-red-50 text-red-600 border-red-200 hover:border-red-300 shadow-xl"
                            >
                                Sign Out
                            </Button>
                        </div>
                    ) : (
                        <Button 
                            onClick={() => setShowAuthModal(true)}
                            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg shadow-xl transition-all duration-200 font-semibold border-2 border-blue-400 hover:border-blue-300 transform hover:scale-105 active:scale-95"
                        >
                            🔐 Sign In
                        </Button>
                    )}
                </div>

                {/* Enhanced Game HUD - Only during drawing game mode */}
                {currentScene === 'game' && (
                    <div className="absolute top-6 left-1/2 transform -translate-x-1/2 flex gap-4">
                        <div className="bg-gradient-to-br from-blue-600/90 to-blue-800/90 backdrop-blur text-white px-4 py-3 rounded-2xl shadow-2xl border border-blue-400/30">
                            <div className="text-xs text-blue-200 font-medium mb-1">Level</div>
                            <div className="font-bold text-2xl text-center">{level === 0 ? "Tutorial" : level}</div>
                        </div>
                        <div className="bg-gradient-to-br from-emerald-600/90 to-emerald-800/90 backdrop-blur text-white px-4 py-3 rounded-2xl shadow-2xl border border-emerald-400/30">
                            <div className="text-xs text-emerald-200 font-medium mb-1">Score</div>
                            <div className="font-bold text-2xl text-center">{score.toLocaleString()}</div>
                        </div>
                        <div className="bg-gradient-to-br from-amber-600/90 to-amber-800/90 backdrop-blur text-white px-4 py-3 rounded-2xl shadow-2xl border border-amber-400/30">
                            <div className="text-xs text-amber-200 font-medium mb-1">Time</div>
                            <div className="font-bold text-2xl text-center">{Math.round(timeLeft)}s</div>
                        </div>

                        {/* Beautiful Ink Progress Bar */}
                        <div className="bg-gradient-to-br from-purple-600/90 to-purple-800/90 backdrop-blur text-white px-4 py-3 rounded-2xl shadow-2xl border border-purple-400/30 min-w-[120px]">
                            <div className="text-xs text-purple-200 font-medium mb-2">Magical Ink</div>
                            <div className="relative">
                                {/* Background bar */}
                                <div className="w-full h-3 bg-purple-900/50 rounded-full overflow-hidden">
                                    {/* Progress bar with color transitions */}
                                    <div 
                                        className={`h-full rounded-full transition-all duration-300 ${
                                            ink > 60 ? 'bg-gradient-to-r from-green-400 to-emerald-500' :
                                            ink > 30 ? 'bg-gradient-to-r from-yellow-400 to-amber-500' :
                                            'bg-gradient-to-r from-red-400 to-red-600'
                                        }`}
                                        style={{ width: `${Math.max(0, ink)}%` }}
                                    />
                                    {/* Shine effect */}
                                    <div className="absolute top-0 left-0 w-full h-full bg-gradient-to-r from-transparent via-white/20 to-transparent animate-pulse" />
                                </div>
                                {/* Percentage text */}
                                <div className="text-center mt-1 font-bold text-sm">{Math.round(ink)}%</div>
                            </div>
                        </div>
                    </div>
                )}

                {/* Enhanced Instructions - Only during drawing phase */}
                {currentScene === 'game' && timeLeft > 5 && (
                    <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 bg-gradient-to-br from-indigo-600/95 to-purple-600/95 backdrop-blur text-white px-8 py-6 rounded-2xl text-center shadow-2xl border border-indigo-400/30 max-w-md">
                        <div className="text-xl font-bold mb-3 text-yellow-300">✨ Draw Phase Active ✨</div>
                        <div className="text-lg font-semibold mb-2">Protect the Capybara!</div>
                        <div className="text-sm text-indigo-100">Drag to draw magical barriers and use your ink wisely</div>
                        <div className="mt-2 text-xs text-indigo-200">🖱️ Mouse • 👆 Touch • 🎯 Strategic placement</div>
                        {level === 0 && (
                            <div className="mt-3 text-sm text-yellow-200 border-t border-indigo-400/30 pt-3">
                                🎓 Tutorial: Press SPACE to change barrier types!<br/>
                                Draw near power-ups to collect them!
                            </div>
                        )}
                    </div>
                )}

                {/* Enhanced Survival phase indicator */}
                {currentScene === 'game' && timeLeft <= 5 && timeLeft > 0 && (
                    <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 bg-gradient-to-br from-red-500 to-red-700 text-white px-8 py-6 rounded-2xl text-center shadow-2xl border-2 border-red-300 animate-pulse">
                        <div className="text-2xl font-bold mb-2 text-yellow-300">🛡️ SURVIVE! 🛡️</div>
                        <div className="text-xl font-bold">{Math.round(timeLeft)} seconds left</div>
                        <div className="text-sm text-red-100 mt-2">Your barriers are protecting the capybara!</div>
                    </div>
                )}

                {/* Not logged in warning during game - temporary */}
                {currentScene === 'game' && !isLoggedIn && showSignInWarning && (
                    <div className="absolute top-24 left-1/2 transform -translate-x-1/2 bg-amber-100 text-amber-800 px-4 py-3 rounded-lg text-center text-sm border border-amber-200 shadow-lg animate-pulse">
                        <div className="flex items-center gap-2">
                            <span>💾</span>
                            <span>Sign in to save your scores</span>
                        </div>
                    </div>
                )}
            </div>

            <AuthModal 
                isOpen={showAuthModal} 
                onClose={() => setShowAuthModal(false)} 
            />
        </>
    );
};

client/src/components/MenuOverlay.tsx.content:
import React, { useState, useEffect } from 'react';
import { Button } from './ui/button';
import { motion, AnimatePresence } from 'framer-motion';
import { Settings } from 'lucide-react';
import { SettingsModal } from './SettingsModal';
const backgroundImage = '/images/AAA_capybara_wetland_background_ab88ce49.png';
const heroImage = '/images/custom_capybara.png';
const barrierImage = '/images/AAA_magical_barrier_UI_b6d0d11f.png';

interface MenuOverlayProps {
    onModeSelect: (mode: 'drawing' | 'tower-defense') => void;
}

const FloatingParticle: React.FC<{ delay: number }> = ({ delay }) => (
    <motion.div
        className="absolute w-1 h-1 bg-green-300 rounded-full opacity-60"
        initial={{ 
            x: Math.random() * window.innerWidth, 
            y: window.innerHeight + 10,
            opacity: 0 
        }}
        animate={{ 
            y: -10,
            opacity: [0, 0.6, 0],
            scale: [0.5, 1, 0.5]
        }}
        transition={{ 
            duration: 8,
            delay,
            repeat: Infinity,
            repeatDelay: Math.random() * 5,
            ease: "linear"
        }}
    />
);

const GlowingText: React.FC<{ children: React.ReactNode; className?: string }> = ({ children, className = "" }) => (
    <div className={`relative ${className}`}>
        <div className="absolute inset-0 text-transparent bg-gradient-to-r from-amber-400 via-yellow-300 to-amber-400 bg-clip-text blur-sm">
            {children}
        </div>
        <div className="relative">{children}</div>
    </div>
);

export const MenuOverlay: React.FC<MenuOverlayProps> = ({ onModeSelect }) => {
    const [isHovered, setIsHovered] = useState(false);
    const [particles, setParticles] = useState<number[]>([]);
    const [showSettings, setShowSettings] = useState(false);

    useEffect(() => {
        // Generate floating particles
        const particleArray = Array.from({ length: 20 }, (_, i) => i);
        setParticles(particleArray);
    }, []);

    return (
        <>
            {/* Background with parallax effect */}
            <div 
                className="absolute inset-0 bg-cover bg-center bg-no-repeat"
                style={{ 
                    backgroundImage: `url(${backgroundImage})`,
                    filter: 'brightness(0.8) contrast(1.1)'
                }}
            />

            {/* Atmospheric overlay */}
            <div className="absolute inset-0 bg-gradient-to-t from-black/40 via-transparent to-black/20" />

            {/* Floating particles */}
            <AnimatePresence>
                {particles.map(particle => (
                    <FloatingParticle key={particle} delay={particle * 0.4} />
                ))}
            </AnimatePresence>

            {/* Magical barrier effect */}
            <div 
                className="absolute inset-0 opacity-30 mix-blend-screen"
                style={{ 
                    backgroundImage: `url(${barrierImage})`,
                    backgroundSize: 'cover',
                    backgroundPosition: 'center',
                    animation: 'pulse 4s infinite ease-in-out'
                }}
            />

            {/* Main content */}
            <div className="absolute inset-0 flex items-center justify-center z-10">
                <motion.div
                    initial={{ scale: 0.5, opacity: 0 }}
                    animate={{ scale: 1, opacity: 1 }}
                    transition={{ 
                        duration: 1.2, 
                        ease: [0.25, 0.46, 0.45, 0.94],
                        staggerChildren: 0.2
                    }}
                    className="flex flex-col items-center max-w-4xl mx-8 text-center"
                >
                    {/* Hero character */}
                    <motion.div
                        initial={{ y: -100, opacity: 0, rotate: -10 }}
                        animate={{ y: 0, opacity: 1, rotate: 0 }}
                        transition={{ 
                            duration: 1.5, 
                            delay: 0.3,
                            type: "spring",
                            stiffness: 100,
                            damping: 10
                        }}
                        whileHover={{ 
                            scale: 1.05,
                            rotate: 2,
                            transition: { duration: 0.3 }
                        }}
                        className="mb-8 relative"
                    >
                        <div className="absolute inset-0 bg-gradient-to-br from-yellow-300/30 to-green-300/30 rounded-full blur-2xl scale-150" />
                        <img 
                            src={heroImage} 
                            alt="Capybara Hero" 
                            className="w-32 h-32 object-contain drop-shadow-2xl relative z-10"
                        />

                        {/* Floating crown effect */}
                        <motion.div
                            className="absolute -top-6 left-1/2 transform -translate-x-1/2 text-4xl"
                            animate={{ 
                                y: [0, -10, 0],
                                rotate: [0, 5, -5, 0]
                            }}
                            transition={{ 
                                duration: 3,
                                repeat: Infinity,
                                ease: "easeInOut"
                            }}
                        >
                            ✨
                        </motion.div>
                    </motion.div>

                    {/* Title */}
                    <motion.div
                        initial={{ y: 50, opacity: 0 }}
                        animate={{ y: 0, opacity: 1 }}
                        transition={{ delay: 0.5, duration: 0.8 }}
                        className="mb-6"
                    >
                        <GlowingText className="text-4xl md:text-5xl font-bold text-white mb-2 leading-tight font-serif" style={{
                            textRendering: 'optimizeLegibility',
                            WebkitFontSmoothing: 'antialiased',
                            MozOsxFontSmoothing: 'grayscale'
                        }}>
                            SAVE THE
                        </GlowingText>
                        <GlowingText className="text-4xl md:text-6xl font-bold bg-gradient-to-r from-amber-400 via-yellow-300 to-amber-400 bg-clip-text text-transparent leading-tight font-serif" style={{
                            textRendering: 'optimizeLegibility',
                            WebkitFontSmoothing: 'antialiased',
                            MozOsxFontSmoothing: 'grayscale'
                        }}>
                            CAPYBARA
                        </GlowingText>

                        {/* Subtitle */}
                        <motion.p 
                            className="text-lg md:text-xl text-amber-100 mt-4 font-light tracking-wide"
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            transition={{ delay: 0.8, duration: 0.6 }}
                        >
                            Master the Ancient Art of Protective Drawing
                        </motion.p>
                    </motion.div>

                    {/* Game mechanics display */}
                    <motion.div
                        initial={{ y: 30, opacity: 0 }}
                        animate={{ y: 0, opacity: 1 }}
                        transition={{ delay: 0.9, duration: 0.6 }}
                        className="mb-10 flex gap-8 text-center"
                    >
                        <div className="backdrop-blur-md bg-blue-600/30 border-2 border-blue-400/50 rounded-xl p-4 transform hover:scale-105 transition-transform">
                            <div className="text-2xl mb-1">⚡</div>
                            <div className="font-bold text-blue-200 text-sm">DRAW PHASE</div>
                            <div className="text-blue-100 text-xs">2.5 Seconds</div>
                        </div>

                        <div className="backdrop-blur-md bg-red-600/30 border-2 border-red-400/50 rounded-xl p-4 transform hover:scale-105 transition-transform">
                            <div className="text-2xl mb-1">🛡️</div>
                            <div className="font-bold text-red-200 text-sm">SURVIVE PHASE</div>
                            <div className="text-red-100 text-xs">5 Seconds</div>
                        </div>
                    </motion.div>

                    {/* Game mode buttons */}
                    <motion.div
                        initial={{ scale: 0, rotate: -180 }}
                        animate={{ scale: 1, rotate: 0 }}
                        transition={{ 
                            delay: 1.2, 
                            duration: 0.8,
                            type: "spring",
                            stiffness: 200,
                            damping: 15
                        }}
                        className="flex flex-col gap-4"
                    >
                        <Button
                            onClick={() => onModeSelect('drawing')}
                            onMouseEnter={() => setIsHovered(true)}
                            onMouseLeave={() => setIsHovered(false)}
                            className="relative group bg-gradient-to-r from-emerald-500 via-green-600 to-emerald-500 hover:from-emerald-400 hover:via-green-500 hover:to-emerald-400 text-white font-bold px-10 py-4 text-xl rounded-2xl shadow-2xl hover:shadow-emerald-500/50 border-2 border-emerald-400 transition-all duration-300 transform hover:scale-110 active:scale-95 overflow-hidden"
                        >
                            {/* Button glow effect */}
                            <div className="absolute inset-0 bg-gradient-to-r from-emerald-400 to-green-400 opacity-0 group-hover:opacity-30 transition-opacity duration-300 blur-xl" />

                            {/* Animated background */}
                            <motion.div
                                className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent"
                                animate={isHovered ? {
                                    x: ['-100%', '200%'],
                                    transition: { duration: 0.8, ease: "easeInOut" }
                                } : {}}
                            />

                            <span className="relative z-10 flex items-center gap-3">
                                <span className="text-3xl">⚔️</span>
                                <span>DRAWING MODE</span>
                                <span className="text-3xl">✨</span>
                            </span>
                        </Button>

                        <Button
                            onClick={() => onModeSelect('tower-defense')}
                            className="relative group bg-gradient-to-r from-blue-500 via-purple-600 to-blue-500 hover:from-blue-400 hover:via-purple-500 hover:to-blue-400 text-white font-bold px-10 py-4 text-xl rounded-2xl shadow-2xl hover:shadow-blue-500/50 border-2 border-blue-400 transition-all duration-300 transform hover:scale-110 active:scale-95 overflow-hidden"
                        >
                            <span className="relative z-10 flex items-center gap-3">
                                <span className="text-3xl">🏰</span>
                                <span>TOWER DEFENSE</span>
                                <span className="text-3xl">🛡️</span>
                            </span>
                        </Button>
                    </motion.div>

                    {/* Instructions */}
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        transition={{ delay: 1.5, duration: 0.6 }}
                        className="mt-8 text-center text-amber-100/80 text-lg"
                    >
                        <p className="font-light tracking-wide">
                            Draw magical barriers with your cursor or finger
                        </p>
                        <p className="text-sm mt-2 text-amber-200/60">
                            Wisdom lies in strategic ink usage
                        </p>
                    </motion.div>
                </motion.div>
            </div>

            {/* Settings button */}
            <motion.button
                initial={{ opacity: 0, scale: 0.5 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ delay: 1.8, duration: 0.5 }}
                onClick={() => setShowSettings(true)}
                className="absolute top-6 right-6 z-20 p-3 bg-slate-800/80 hover:bg-slate-700/80 backdrop-blur-sm rounded-full text-white hover:text-amber-300 transition-all duration-300 shadow-lg hover:shadow-xl border border-slate-600/50"
            >
                <Settings className="w-6 h-6" />
            </motion.button>

            {/* Settings Modal */}
            <SettingsModal 
                isOpen={showSettings} 
