      <div className="relative z-10 text-center">
        {/* Enhanced floating elements with better animations */}
        <motion.div className="mb-8">
          <div className="w-72 h-72 relative">
            {/* Rotating sparkles */}
            <motion.div
              className="absolute inset-0"
              animate={{ rotate: 360 }}
              transition={{ duration: 8, repeat: Infinity, ease: "linear" }}
            >
              <div className="absolute top-0 left-1/2 transform -translate-x-1/2">
                <motion.div
                  className="text-4xl"
                  animate={{ scale: [1, 1.3, 1], opacity: [0.7, 1, 0.7] }}
                  transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
                >✨</motion.div>
              </div>
              <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2">
                <motion.div
                  className="text-4xl"
                  animate={{ scale: [1, 1.3, 1], opacity: [0.7, 1, 0.7] }}
                  transition={{ duration: 2, repeat: Infinity, ease: "easeInOut", delay: 0.5 }}
                >✨</motion.div>
              </div>
              <div className="absolute left-0 top-1/2 transform -translate-y-1/2">
                <motion.div
                  className="text-4xl"
                  animate={{ scale: [1, 1.3, 1], opacity: [0.7, 1, 0.7] }}
                  transition={{ duration: 2, repeat: Infinity, ease: "easeInOut", delay: 1 }}
                >✨</motion.div>
              </div>
              <div className="absolute right-0 top-1/2 transform -translate-y-1/2">
                <motion.div
                  className="text-4xl"
                  animate={{ scale: [1, 1.3, 1], opacity: [0.7, 1, 0.7] }}
                  transition={{ duration: 2, repeat: Infinity, ease: "easeInOut", delay: 1.5 }}
                >✨</motion.div>
              </div>
            </motion.div>

            {/* Floating bees with more natural movement */}
            <motion.div
              className="absolute top-1/4 right-1/4"
              animate={{ 
                x: [0, 20, -10, 15, 0], 
                y: [0, -15, 10, -5, 0],
                rotate: [0, 10, -5, 8, 0]
              }}
              transition={{ duration: 4, repeat: Infinity, ease: "easeInOut" }}
            >
              <div className="text-3xl">🐝</div>
            </motion.div>

            <motion.div
              className="absolute bottom-1/4 left-1/4"
              animate={{ 
                x: [0, -15, 20, -10, 0], 
                y: [0, 10, -20, 15, 0],
                rotate: [0, -8, 12, -6, 0]
              }}
              transition={{ duration: 3.5, repeat: Infinity, ease: "easeInOut", delay: 1 }}
            >
              <div className="text-3xl">🐝</div>
            </motion.div>

            {/* Additional floating magical elements */}
            <motion.div
              className="absolute top-1/3 left-1/3 text-2xl opacity-60"
              animate={{ 
                y: [0, -20, 0], 
                scale: [1, 1.1, 1],
                opacity: [0.4, 0.8, 0.4]
              }}
              transition={{ duration: 3, repeat: Infinity, ease: "easeInOut", delay: 2 }}
            >🌟</motion.div>

            <motion.div
              className="absolute bottom-1/3 right-1/3 text-2xl opacity-60"
              animate={{ 
                y: [0, 20, 0], 
                scale: [1, 1.1, 1],
                opacity: [0.4, 0.8, 0.4]
              }}
              transition={{ duration: 2.5, repeat: Infinity, ease: "easeInOut", delay: 1.5 }}
            >🌟</motion.div>
          </div>
        </motion.div>

        {/* Loading text */}
        <motion.h2 
          className="text-3xl font-bold text-amber-100 mb-8"
          key={loadingText}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          {loadingText}
        </motion.h2>

        {/* Progress bar */}
        <div className="w-80 mx-auto">
          <div className="bg-amber-900/30 rounded-full h-4 backdrop-blur-sm border border-amber-500/30">
            <motion.div 
              className="bg-gradient-to-r from-amber-400 via-yellow-400 to-amber-400 h-full rounded-full relative overflow-hidden"
              style={{ width: `${progress}%` }}
              initial={{ width: 0 }}
              animate={{ width: `${progress}%` }}
              transition={{ duration: 0.3 }}
            >
              {/* Animated shimmer effect */}
              <motion.div
                className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent"
                animate={{ x: ['-100%', '200%'] }}
                transition={{ duration: 1.5, repeat: Infinity, ease: "easeInOut" }}
              />
            </motion.div>
          </div>

          {/* Progress percentage */}
          <motion.p 
            className="text-amber-200 text-lg mt-4 font-light"
            animate={{ opacity: [1, 0.5, 1] }}
            transition={{ duration: 1.5, repeat: Infinity }}
          >
            {progress}%
          </motion.p>
        </div>
      </div>
    </div>
  );
};

client/src/utils/seedPhraseGenerator.ts.content:
// Word list for generating 4-word seed phrases
const WORD_LIST = [
    // Animals
    'capybara', 'dolphin', 'elephant', 'falcon', 'giraffe', 'hamster', 'iguana', 'jaguar',
    'kangaroo', 'lemur', 'mongoose', 'narwhal', 'octopus', 'penguin', 'quokka', 'rabbit',
    'seahorse', 'turtle', 'unicorn', 'vulture', 'walrus', 'xerus', 'yak', 'zebra',

    // Nature
    'aurora', 'breeze', 'crystal', 'dewdrop', 'ember', 'forest', 'galaxy', 'horizon',
    'island', 'jungle', 'lagoon', 'meadow', 'nebula', 'ocean', 'peak', 'quartz',
    'river', 'storm', 'thunder', 'valley', 'wave', 'zephyr',

    // Objects
    'anchor', 'beacon', 'compass', 'diamond', 'engine', 'feather', 'guardian', 'harbor',
    'ivory', 'jewel', 'key', 'lantern', 'mirror', 'needle', 'orb', 'prism',
    'quest', 'relic', 'shield', 'torch', 'unity', 'vessel', 'wand', 'zenith',

    // Colors & Elements
    'amber', 'bronze', 'copper', 'emerald', 'flame', 'gold', 'iron', 'jade',
    'lunar', 'marble', 'onyx', 'pearl', 'ruby', 'silver', 'topaz', 'violet'
];

export const generateSeedPhrase = (): string => {
    const shuffled = [...WORD_LIST].sort(() => Math.random() - 0.5);
    return shuffled.slice(0, 4).join(' ');
};

export const validateSeedPhrase = (phrase: string): boolean => {
    const words = phrase.trim().toLowerCase().split(' ');
    return words.length === 4 && words.every(word => WORD_LIST.includes(word));
};

export const formatSeedPhrase = (phrase: string): string => {
    return phrase.toLowerCase().trim().replace(/\s+/g, ' ');
};

client/src/utils/solanaValidator.ts.content:
import { PublicKey } from '@solana/web3.js';

export const validateSolanaWallet = (address: string): boolean => {
    try {
        // Check if it's a valid base58 Solana public key
        new PublicKey(address);
        return true;
    } catch (error) {
        return false;
    }
};

export const isValidSolanaAddress = (address: string): boolean => {
    // Allow any non-empty string as wallet address for maximum compatibility
    if (!address || typeof address !== 'string') {
        return false;
    }

    // Trim whitespace
    const trimmedAddress = address.trim();

    // Just check it's not empty and has reasonable length
    if (trimmedAddress.length < 1 || trimmedAddress.length > 100) {
        return false;
    }

    // Accept any alphanumeric string for maximum compatibility
    return true;
};

export const formatSolanaAddress = (address: string): string => {
    if (!address || address.length < 8) return address;
    return `${address.slice(0, 4)}...${address.slice(-4)}`;
};

