import React, { useEffect, useRef, useState } from 'react';
import { Button } from './ui/button';
import { Loader2, Play, RotateCcw } from 'lucide-react';

interface UnityGameLoaderProps {
  onScoreUpdate?: (score: number) => void;
  onGameComplete?: (survivalTime: number, capybarasProtected: number, finalScore: number) => void;
  onCapybaraSaved?: (count: number) => void;
  onLoadingProgress?: (progress: number) => void;
  onGameReady?: () => void;
  showLoadingScreen?: boolean;
}

export const UnityGameLoader: React.FC<UnityGameLoaderProps> = ({
  onScoreUpdate,
  onGameComplete,
  onCapybaraSaved,
  onLoadingProgress,
  onGameReady,
  showLoadingScreen = true
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [loadingProgress, setLoadingProgress] = useState(0);
  const [error, setError] = useState<string | null>(null);
  const [gameInstance, setGameInstance] = useState<any>(null);

  useEffect(() => {
    // Listen for Unity messages
    const handleUnityMessage = (event: MessageEvent) => {
      if (event.data && typeof event.data === 'object') {
        switch (event.data.type) {
          case 'UNITY_SCORE_UPDATE':
            onScoreUpdate?.(event.data.score);
            break;
          case 'UNITY_GAME_COMPLETE':
            onGameComplete?.(
              event.data.survivalTime,
              event.data.capybarasProtected,
              event.data.finalScore
            );
            break;
          case 'UNITY_CAPYBARA_SAVED':
            onCapybaraSaved?.(event.data.count);
            break;
        }
      }
    };

    window.addEventListener('message', handleUnityMessage);
    return () => window.removeEventListener('message', handleUnityMessage);
  }, [onScoreUpdate, onGameComplete, onCapybaraSaved]);

  const loadUnityGame = async () => {
    if (!canvasRef.current) return;

    try {
      setIsLoading(true);
      setError(null);

      // Check if Unity WebGL files exist
      const buildUrl = '/unity-build/Build';

      // Create Unity configuration  
      const config = {
        dataUrl: `${buildUrl}/WebGLBuild.data.br`,
        frameworkUrl: `${buildUrl}/WebGLBuild.framework.js.br`,
        codeUrl: `${buildUrl}/WebGLBuild.wasm.br`,
        loaderUrl: `${buildUrl}/WebGLBuild.loader.js`,
        streamingAssetsUrl: 'StreamingAssets',
        companyName: 'Save Capybara Team',
        productName: 'Save the Capybara',
        productVersion: '1.0.0',
      };

      // Load Unity game
      const canvas = canvasRef.current;
      const loadingBar = document.getElementById('unity-loading-bar');
      const progressBarFull = document.getElementById('unity-progress-bar-full');

      // @ts-ignore - Unity WebGL runtime
      window.createUnityInstance(canvas, config, (progress: number) => {
        const progressPercent = Math.round(progress * 100);
        setLoadingProgress(progressPercent);
        onLoadingProgress?.(progressPercent);
        if (progressBarFull) {
          progressBarFull.style.width = 100 * progress + '%';
        }
      }).then((unityInstance: any) => {
        setGameInstance(unityInstance);
        setIsLoading(false);
        onGameReady?.();
        if (loadingBar) {
          loadingBar.style.display = 'none';
        }
      }).catch((message: string) => {
        setError(`Failed to load Unity game: ${message}`);
        setIsLoading(false);
      });

    } catch (err) {
      setError('Failed to initialize Unity WebGL');
      setIsLoading(false);
    }
  };

  const restartGame = () => {
    if (gameInstance) {
      // Send restart message to Unity
      gameInstance.SendMessage('WebGLRewardManager', 'StartGame');
    }
  };

  return (
    <div className="unity-game-container relative w-full h-full bg-black rounded-lg overflow-hidden">
      {/* Unity Canvas */}
      <canvas
        ref={canvasRef}
        id="unity-canvas"
        className="w-full h-full block"
        style={{
          width: '100%',
          height: '100%',
          background: 'linear-gradient(135deg, #1a1a2e, #16213e, #0f3460)',
        }}
      />

      {/* Loading Screen */}
      {isLoading && (
        <div className="absolute inset-0 flex flex-col items-center justify-center bg-gradient-to-br from-amber-900/90 via-orange-900/90 to-red-900/90 backdrop-blur-sm">
          <div className="text-center max-w-md px-6">
            <div className="mb-6">
              <div className="w-20 h-20 mx-auto mb-4 rounded-full bg-amber-500/20 flex items-center justify-center">
                <Loader2 className="w-10 h-10 text-amber-300 animate-spin" />
              </div>
              <h3 className="text-2xl font-bold text-white mb-2">Loading Capybara Adventure</h3>
              <p className="text-amber-200 text-sm">Preparing your island-hopping experience...</p>
            </div>

            {/* Loading Progress Bar */}
            <div id="unity-loading-bar" className="w-full bg-amber-800/30 rounded-full h-3 mb-4">
              <div
                id="unity-progress-bar-full"
                className="bg-gradient-to-r from-amber-400 to-orange-500 h-3 rounded-full transition-all duration-300"
                style={{ width: `${loadingProgress}%` }}
              />
            </div>
            <div className="text-amber-100 text-sm">{loadingProgress}% loaded</div>
          </div>
        </div>
      )}

      {/* Error Screen */}
      {error && (
        <div className="absolute inset-0 flex flex-col items-center justify-center bg-red-900/90 backdrop-blur-sm text-white p-6">
          <div className="text-center max-w-md">
            <h3 className="text-xl font-bold mb-4">Game Loading Failed</h3>
            <p className="text-red-200 mb-6 text-sm">{error}</p>
            <Button 
              onClick={loadUnityGame}
              className="bg-red-600 hover:bg-red-700 text-white"
            >
              <RotateCcw className="w-4 h-4 mr-2" />
              Retry Loading
            </Button>
          </div>
        </div>
      )}

      {/* Game Controls (shown when game is loaded) */}
      {!isLoading && !error && (
        <div className="absolute top-4 right-4 flex gap-2">
          <Button
            onClick={restartGame}
            size="sm"
            className="bg-emerald-600 hover:bg-emerald-700 text-white shadow-lg"
          >
            <RotateCcw className="w-4 h-4" />
          </Button>
        </div>
      )}

      {/* Initial Load Button */}
      {!gameInstance && !isLoading && !error && (
        <div className="absolute inset-0 flex items-center justify-center bg-gradient-to-br from-amber-900/80 via-orange-900/80 to-emerald-900/80 backdrop-blur-sm">
          <Button
            onClick={loadUnityGame}
            size="lg"
            className="bg-gradient-to-r from-amber-600 to-orange-600 hover:from-amber-700 hover:to-orange-700 text-white font-bold py-4 px-8 rounded-lg shadow-xl transform hover:scale-105 transition-all"
          >
            <Play className="w-6 h-6 mr-3" />
            Start Capybara Adventure
          </Button>
        </div>
      )}
    </div>
  );
};

