import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { UnityGameLoader } from './UnityGameLoader';

const backgroundImage = '/images/AAA_capybara_wetland_background_ab88ce49.png';
const heroImage = '/images/AAA_capybara_hero_character_0a40b727.png';

interface UnityLoadingScreenProps {
  onScoreUpdate?: (score: number) => void;
  onGameComplete?: (survivalTime: number, capybarasProtected: number, finalScore: number) => void;
  onCapybaraSaved?: (count: number) => void;
}

export const UnityLoadingScreen: React.FC<UnityLoadingScreenProps> = ({
  onScoreUpdate,
  onGameComplete,
  onCapybaraSaved
}) => {
  const [progress, setProgress] = useState(0);
  const [loadingText, setLoadingText] = useState('Loading Capybara Adventure...');
  const [gameLoaded, setGameLoaded] = useState(false);
  const [showGame, setShowGame] = useState(false);

  const loadingMessages = [
    'Loading Capybara Adventure...',
    'Preparing island worlds...',
    'Setting up precision jumping mechanics...',
    'Connecting reward system...',
    'Ready to jump!'
  ];

  const handleUnityLoadingProgress = (unityProgress: number) => {
    setProgress(unityProgress);

    // Update loading text based on progress
    const messageIndex = Math.floor((unityProgress / 100) * loadingMessages.length);
    if (messageIndex < loadingMessages.length) {
      setLoadingText(loadingMessages[messageIndex]);
    }
  };

  const handleGameReady = () => {
    setGameLoaded(true);
    setTimeout(() => {
      setShowGame(true);
    }, 500);
  };

  // Auto-start Unity loading
  useEffect(() => {
    // Start loading immediately
    console.log('Unity loading screen mounted');
  }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center relative overflow-hidden">
      {/* Background */}
      <div 
        className="absolute inset-0 bg-cover bg-center bg-no-repeat"
        style={{ 
          backgroundImage: `url(${backgroundImage})`,
          filter: 'brightness(0.6) contrast(1.1)'
        }}
      />

      {/* Overlay */}
      <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-black/40" />

      {/* Unity Game Canvas (hidden behind loading screen initially) */}
      {showGame && (
        <div className="absolute inset-0 z-10">
          <UnityGameLoader 
            onLoadingProgress={handleUnityLoadingProgress}
            onGameReady={handleGameReady}
            onScoreUpdate={onScoreUpdate}
            onGameComplete={onGameComplete}
            onCapybaraSaved={onCapybaraSaved}
            showLoadingScreen={false}
          />
        </div>
      )}

      {/* Loading Screen Overlay */}
      <div className={`absolute inset-0 z-20 flex flex-col items-center justify-center transition-opacity duration-1000 ${showGame ? 'opacity-0 pointer-events-none' : 'opacity-100'}`}>
        <div className="relative z-10 text-center">
          {/* Capybara character with loading animation */}
          <motion.div
            className="mb-8"
            animate={{ 
              y: [0, -10, 0],
              rotate: [0, 2, -2, 0]
            }}
            transition={{ 
              duration: 2,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          >
            <div className="relative">
              <div className="absolute inset-0 bg-gradient-to-br from-yellow-300/40 to-green-300/40 rounded-full blur-3xl scale-150" />
              <img 
                src={heroImage} 
                alt="Loading Capybara" 
                className="w-48 h-48 object-contain drop-shadow-2xl relative z-10"
              />

              {/* Spinning sparkles */}
              <motion.div
                className="absolute inset-0 flex items-center justify-center"
                animate={{ rotate: 360 }}
                transition={{ duration: 3, repeat: Infinity, ease: "linear" }}
              >
                <div className="w-64 h-64 relative">
                  <div className="absolute top-0 left-1/2 transform -translate-x-1/2 text-2xl">✨</div>
                  <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 text-2xl">✨</div>
                  <div className="absolute left-0 top-1/2 transform -translate-y-1/2 text-2xl">✨</div>
                  <div className="absolute right-0 top-1/2 transform -translate-y-1/2 text-2xl">✨</div>
                </div>
              </motion.div>
            </div>
          </motion.div>

          {/* Loading text */}
          <motion.h2 
            className="text-3xl font-bold text-amber-100 mb-8"
            key={loadingText}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            {loadingText}
          </motion.h2>

          {/* Progress bar */}
          <div className="w-80 mx-auto">
            <div className="bg-amber-900/30 rounded-full h-4 backdrop-blur-sm border border-amber-500/30">
              <motion.div 
                className="bg-gradient-to-r from-amber-400 via-yellow-400 to-amber-400 h-full rounded-full relative overflow-hidden"
                style={{ width: `${progress}%` }}
                initial={{ width: 0 }}
                animate={{ width: `${progress}%` }}
                transition={{ duration: 0.3 }}
              >
                {/* Animated shimmer effect */}
                <motion.div
                  className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent"
                  animate={{ x: ['-100%', '200%'] }}
                  transition={{ duration: 1.5, repeat: Infinity, ease: "easeInOut" }}
                />
              </motion.div>
            </div>

            {/* Progress percentage */}
            <motion.p 
              className="text-amber-200 text-lg mt-4 font-light"
              animate={{ opacity: [1, 0.5, 1] }}
              transition={{ duration: 1.5, repeat: Infinity }}
            >
              {progress}%
            </motion.p>
          </div>
        </div>
      </div>
    </div>
  );
};

