using System.Runtime.InteropServices;
using UnityEngine;
using SaveTheCapybara.Player;

namespace SaveTheCapybara.WebGL
{
    public class WebGLRewardManager : MonoBehaviour
    {
        [DllImport("__Internal")]
        private static extern void SendScoreToReact(int score);

        [DllImport("__Internal")]
        private static extern void SendGameCompleteToReact(int survivalTime, int capybarasProtected, int finalScore);

        [DllImport("__Internal")]
        private static extern void SendCapybaraSavedToReact(int count);

        private static WebGLRewardManager instance;
        public static WebGLRewardManager Instance => instance;

        private int currentScore = 0;
        private int capybarasProtected = 0;
        private float gameStartTime;
        private bool gameInProgress = false;

        private void Awake()
        {
            if (instance == null)
            {
                instance = this;
                DontDestroyOnLoad(gameObject);
            }
            else
            {
                Destroy(gameObject);
            }
        }

        public void StartGame()
        {
            gameStartTime = Time.time;
            gameInProgress = true;
            currentScore = 0;
            capybarasProtected = 0;
            Debug.Log("Game started - tracking rewards");
        }

        public void UpdateScore(int newScore)
        {
            currentScore = newScore;

            #if UNITY_WEBGL && !UNITY_EDITOR
            try 
            {
                SendScoreToReact(newScore);
            }
            catch (System.Exception e)
            {
                Debug.LogWarning("Failed to send score to React: " + e.Message);
            }
            #endif
        }

        public void CapybaraSaved()
        {
            capybarasProtected++;

            #if UNITY_WEBGL && !UNITY_EDITOR
            try 
            {
                SendCapybaraSavedToReact(1);
            }
            catch (System.Exception e)
            {
                Debug.LogWarning("Failed to send capybara saved to React: " + e.Message);
            }
            #endif
        }

        public void GameComplete(bool success = true)
        {
            if (!gameInProgress) return;

            gameInProgress = false;
            int survivalTime = Mathf.FloorToInt(Time.time - gameStartTime);

            // If game failed, capybarasProtected should be 0 for reward calculation
            if (!success) 
            {
                capybarasProtected = 0;
            }

            #if UNITY_WEBGL && !UNITY_EDITOR
            try 
            {
                SendGameCompleteToReact(survivalTime, capybarasProtected, currentScore);
                Debug.Log($"Game completed: Time={survivalTime}s, Capybaras={capybarasProtected}, Score={currentScore}");
            }
            catch (System.Exception e)
            {
                Debug.LogWarning("Failed to send game complete to React: " + e.Message);
            }
            #else
            Debug.Log($"[Reward System] Game completed: Time={survivalTime}s, Capybaras={capybarasProtected}, Score={currentScore}");
            Debug.Log($"[Reward System] Would earn rewards based on performance");
            #endif
        }

        // Call this when capybara reaches safe zone
        public void OnCapybaraReachedSafety()
        {
            CapybaraSaved();
        }

        // Call this when capybara falls or dies
        public void OnCapybaraFailed()
        {
            GameComplete(false);
        }

        // Call this from your score system
        public void OnScoreChanged(int newScore)
        {
            UpdateScore(newScore);
        }

        // Call this when level/wave is completed successfully
        public void OnLevelComplete()
        {
            GameComplete(true);
        }
    }
}

