import React, { useRef, useEffect, useState, useCallback } from 'react';
import * as THREE from 'three';
import { FBXLoader } from 'three/examples/jsm/loaders/FBXLoader.js';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader.js';
import { Button } from './ui/button';
import { Card } from './ui/card';
import { Trophy, ArrowLeft, Heart, Zap, Play, Pause, RotateCcw } from 'lucide-react';

interface CapiRushGameProps {
  onBackToMenu?: () => void;
}

interface GameStats {
  score: number;
  highScore: number;
  gamesPlayed: number;
}

export const CapiRushGame: React.FC<CapiRushGameProps> = ({ onBackToMenu }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const gameRef = useRef<any>(null);
  const animationFrameRef = useRef<number>(0);

  const [gameStats, setGameStats] = useState<GameStats>(() => {
    const saved = localStorage.getItem('capirush-stats');
    return saved ? JSON.parse(saved) : {
      score: 0,
      highScore: 0,
      gamesPlayed: 0
    };
  });

  const [isGameRunning, setIsGameRunning] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [loadingProgress, setLoadingProgress] = useState(0);
  const [currentScore, setCurrentScore] = useState(0);

  // Box helper class from CapiRush
  class Box extends THREE.Mesh {
    width: number;
    height: number;
    depth: number;
    bottom: number;
    top: number;

    constructor({ width, height, depth, position = { x: 0, y: 0, z: 0 } }: {
      width: number;
      height: number;
      depth: number;
      position?: { x: number; y: number; z: number };
    }) {
      super(new THREE.BoxGeometry(width, height, depth));

      this.width = width;
      this.height = height;
      this.depth = depth;

      this.position.set(position.x, position.y, position.z);

      this.bottom = this.position.y - this.height / 2;
      this.top = this.position.y + this.height / 2;
    }
  }

  const initializeGame = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    setIsLoading(true);
    setLoadingProgress(0);

    // Initialize Three.js scene - following CapiRush structure
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, canvas.clientWidth / canvas.clientHeight, 0.1, 100);
    const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });

    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(canvas.clientWidth, canvas.clientHeight);
    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFSoftShadowMap;

    camera.position.set(0, 2, 5);

    // Game state
    const gameState = {
      scene,
      camera: camera as any,
      renderer,
      score: 0,
      gameRunning: true,
      player: null as any,
      obstacles: [] as any[],
      outObjects: [] as any[],
      keys: {} as { [key: string]: boolean },
      lastTime: 0,
      gravForce: 0.05,
      playerOnGround: true,
      dayNightCycle: 0,
      camPos: 1,
      cleanup: () => {
        if (animationFrameRef.current) {
          cancelAnimationFrame(animationFrameRef.current);
        }
        window.removeEventListener('keydown', handleKeyDown);
        window.removeEventListener('keyup', handleKeyUp);
        window.removeEventListener('resize', handleResize);
      }
    };

    // Simulate loading progress
    const simulateLoading = () => {
      let progress = 0;
      const loadingInterval = setInterval(() => {
        progress += Math.random() * 15;
        setLoadingProgress(Math.min(progress, 100));

        if (progress >= 100) {
          clearInterval(loadingInterval);
          setIsLoading(false);
          // Start the actual game immediately
          setTimeout(() => {
            startGameLoop();
          }, 100);
        }
      }, 50);
    };

    // Setup skybox with meadow theme
    const setupSkybox = () => {
      // Create a simple gradient background
      scene.background = new THREE.Color(0x87CEEB);
    };

    // Setup scenario - road and walkway
    const setupScenario = () => {
      // Road
      const roadGeometry = new THREE.BoxGeometry(5, 0.5, 50);
      const roadMaterial = new THREE.MeshBasicMaterial({ color: 0x333333 });
      const road = new THREE.Mesh(roadGeometry, roadMaterial);
      road.position.set(0, -1.5, -10);
      scene.add(road);

      // Walkway
      const walkwayGeometry = new THREE.BoxGeometry(9, 0.5, 50);
      const walkwayMaterial = new THREE.MeshBasicMaterial({ color: 0x228B22 });
      const walkway = new THREE.Mesh(walkwayGeometry, walkwayMaterial);
      walkway.position.set(0, -1.6, -10);
      scene.add(walkway);
    };

    // Setup lights - simplified
    const setupLights = () => {
      const ambientLight = new THREE.AmbientLight(0xffffff, 0.8);
      scene.add(ambientLight);

      const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
      directionalLight.position.set(0, 5, 5);
      scene.add(directionalLight);
    };

    // Create capybara player
    const createPlayer = () => {
      // Simple capybara representation
      const playerGeometry = new THREE.BoxGeometry(0.6, 0.4, 0.8);
      const playerMaterial = new THREE.MeshBasicMaterial({ color: 0x8B4513 });
      const player = new THREE.Mesh(playerGeometry, playerMaterial);
      player.position.set(0, -0.8, 0);

      // Add some details to make it look more like a capybara
      const headGeometry = new THREE.BoxGeometry(0.3, 0.25, 0.35);
      const headMaterial = new THREE.MeshBasicMaterial({ color: 0x654321 });
      const head = new THREE.Mesh(headGeometry, headMaterial);
      head.position.set(0.35, 0.1, 0);
      player.add(head);

      scene.add(player);
      return player;
    };

    // Create obstacles (cars, balls, tires)
    const createObstacle = (type: string = 'car') => {
      let obstacle;
      if (type === 'car') {
        obstacle = new THREE.Mesh(
          new THREE.BoxGeometry(0.8, 0.4, 1.6),
          new THREE.MeshBasicMaterial({ color: 0xFF0000 })
        );
      } else if (type === 'ball') {
        obstacle = new THREE.Mesh(
          new THREE.SphereGeometry(0.3),
          new THREE.MeshBasicMaterial({ color: 0x00FF00 })
        );
      } else { // tire
        obstacle = new THREE.Mesh(
          new THREE.CylinderGeometry(0.25, 0.25, 0.15, 8),
          new THREE.MeshBasicMaterial({ color: 0x222222 })
        );
      }

      obstacle.position.set(
        (Math.random() - 0.5) * 4,
        -0.8,
        -5 - Math.random() * 5
      );
      scene.add(obstacle);
      return obstacle;
    };

    // Create objects for walkway decoration
    const createOutObject = () => {
      const colors = [0x228B22, 0x32CD32, 0x9ACD32];
      const object = new THREE.Mesh(
        new THREE.BoxGeometry(0.3, 0.8, 0.3),
        new THREE.MeshBasicMaterial({ color: colors[Math.floor(Math.random() * colors.length)] })
      );
      object.position.set(
        (Math.random() > 0.5 ? 1 : -1) * (3 + Math.random() * 1.5),
        -0.8,
        -5 - Math.random() * 5
      );
      scene.add(object);
      return object;
    };

    // Input handling
    const handleKeyDown = (e: KeyboardEvent) => {
      gameState.keys[e.key.toLowerCase()] = true;
      gameState.keys[e.code] = true;

      // Jump logic
      if ((e.key.toLowerCase() === 'w' || e.code === 'ArrowUp' || e.code === 'Space') && gameState.playerOnGround) {
        if (gameState.player) {
          gameState.player.userData.velocityY = 0.8;
          gameState.playerOnGround = false;
        }
      }

      // Camera toggle
      if (e.key.toLowerCase() === 'c') {
        toggleCamera();
      }

      e.preventDefault();
    };

    const handleKeyUp = (e: KeyboardEvent) => {
      gameState.keys[e.key.toLowerCase()] = false;
      gameState.keys[e.code] = false;
      e.preventDefault();
    };

    const handleResize = () => {
      if (!canvas) return;
      camera.aspect = canvas.clientWidth / canvas.clientHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(canvas.clientWidth, canvas.clientHeight);
    };

    const toggleCamera = () => {
      if (gameState.camPos === 1) {
        // Orthographic top view
        const zoom = 50;
        const newCamera = new THREE.OrthographicCamera(
          canvas.clientWidth / -2 / zoom,
          canvas.clientWidth / 2 / zoom,
          canvas.clientHeight / 2 / zoom,
          canvas.clientHeight / -2 / zoom,
          -10,
          1000
        );
        newCamera.position.set(0, 10, 0);
        newCamera.lookAt(0, 0, 0);
        gameState.camera = newCamera;
        gameState.camPos = 2;
      } else {
        // Perspective view
        const newCamera = new THREE.PerspectiveCamera(75, canvas.clientWidth / canvas.clientHeight, 0.1, 100);
        newCamera.position.set(0, 0, 5.3);
        gameState.camera = newCamera;
        gameState.camPos = 1;
      }
    };

    // Collision detection
    const checkCollision = (obj1: any, obj2: any) => {
      const distance = obj1.position.distanceTo(obj2.position);
      return distance < 0.8;
    };

    // Game loop
    const gameLoop = (currentTime: number) => {
      if (!gameState.gameRunning || isPaused) {
        if (gameState.gameRunning && isPaused) {
          animationFrameRef.current = requestAnimationFrame(gameLoop);
        }
        return;
      }

      const deltaTime = currentTime - gameState.lastTime;
      gameState.lastTime = currentTime;
      const modifier = deltaTime / 1000;

      // Update score
      gameState.score += Math.floor(modifier * 10);
      setCurrentScore(gameState.score);

      // Player movement
      if (gameState.player) {
        const speed = 3;

        // Horizontal movement
        if (gameState.keys['a'] || gameState.keys['arrowleft']) {
          gameState.player.position.x = Math.max(-2, gameState.player.position.x - speed * modifier);
        }
        if (gameState.keys['d'] || gameState.keys['arrowright']) {
          gameState.player.position.x = Math.min(2, gameState.player.position.x + speed * modifier);
        }

        // Gravity and jumping
        if (!gameState.playerOnGround) {
          gameState.player.userData.velocityY -= gameState.gravForce * modifier * 60;
          gameState.player.position.y += gameState.player.userData.velocityY * modifier * 10;

          if (gameState.player.position.y <= -0.8) {
            gameState.player.position.y = -0.8;
            gameState.playerOnGround = true;
            gameState.player.userData.velocityY = 0;
          }
        } else {
          gameState.player.userData.velocityY = 0;
        }
      }

      // Move obstacles and check collisions
      gameState.obstacles.forEach((obstacle, index) => {
        obstacle.position.z += 8 * modifier;

        // Rotate balls and tires
        if (obstacle.geometry instanceof THREE.SphereGeometry || 
            obstacle.geometry instanceof THREE.CylinderGeometry) {
          obstacle.rotation.x += 5 * modifier;
        }

        // Remove obstacles that passed the player
        if (obstacle.position.z > 10) {
          scene.remove(obstacle);
          gameState.obstacles.splice(index, 1);
          gameState.score += 50;
        }

        // Check collision
        if (gameState.player && checkCollision(gameState.player, obstacle)) {
          endGame(gameState.score);
          return;
        }
      });

      // Move out objects (decorations)
      gameState.outObjects.forEach((obj, index) => {
        obj.position.z += 8 * modifier;
        obj.rotation.y += 2 * modifier;

        if (obj.position.z > 10) {
          scene.remove(obj);
          gameState.outObjects.splice(index, 1);
        }
      });

      // Spawn new obstacles
      if (Math.random() < 0.015) {
        const types = ['car', 'ball', 'tire'];
        const obstacle = createObstacle(types[Math.floor(Math.random() * types.length)]);
        gameState.obstacles.push(obstacle);
      }

      // Spawn decorative objects
      if (Math.random() < 0.01) {
        const outObj = createOutObject();
        gameState.outObjects.push(outObj);
      }

      // Day/night cycle simulation
      gameState.dayNightCycle += modifier * 0.1;
      const lightIntensity = 0.25 + Math.sin(gameState.dayNightCycle) * 0.15;
      scene.children.forEach(child => {
        if (child instanceof THREE.DirectionalLight) {
          child.intensity = lightIntensity;
        }
      });

      renderer.render(scene, gameState.camera);
      animationFrameRef.current = requestAnimationFrame(gameLoop);
    };

    const startGameLoop = () => {
      // Initialize everything
      setupSkybox();
      setupScenario();
      setupLights();
      gameState.player = createPlayer();
      gameState.player.userData = { velocityY: 0 };

      // Start with some obstacles and decorations
      for (let i = 0; i < 3; i++) {
        gameState.obstacles.push(createObstacle());
        gameState.outObjects.push(createOutObject());
      }

      // Make sure canvas is properly sized and render first frame
      renderer.setSize(canvas.clientWidth, canvas.clientHeight);
      renderer.render(scene, gameState.camera);

      animationFrameRef.current = requestAnimationFrame(gameLoop);
    };

    const endGame = (finalScore: number) => {
      gameState.gameRunning = false;
      setIsGameRunning(false);
      const newHighScore = Math.max(gameStats.highScore, finalScore);
      const newStats = {
        score: finalScore,
        highScore: newHighScore,
        gamesPlayed: gameStats.gamesPlayed + 1
      };

      setGameStats(newStats);
      localStorage.setItem('capirush-stats', JSON.stringify(newStats));
    };

    // Add event listeners
    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);
    window.addEventListener('resize', handleResize);

    gameRef.current = gameState;
    simulateLoading();
  }, [gameStats, isPaused]);

  const startGame = () => {
    setIsGameRunning(true);
    setIsPaused(false);
    setCurrentScore(0);
    initializeGame();
  };

  const pauseResumeGame = () => {
    setIsPaused(prev => !prev);
  };

  const resetGame = () => {
    if (gameRef.current?.cleanup) {
      gameRef.current.cleanup();
    }
    setIsGameRunning(false);
    setIsPaused(false);
    setCurrentScore(0);
  };

  useEffect(() => {
    return () => {
      if (gameRef.current?.cleanup) {
        gameRef.current.cleanup();
      }
    };
  }, []);

  return (
    <div className="min-h-screen p-4 bg-gradient-to-br from-teal-600 via-teal-700 to-teal-800">
      {/* Header */}
      <div className="max-w-6xl mx-auto mb-6">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-4">
            {onBackToMenu && (
              <Button onClick={onBackToMenu} variant="outline" size="sm" className="bg-white/10 border-white/30" data-testid="button-back-menu">
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Menu
              </Button>
            )}
            <h1 className="text-3xl font-bold text-white flex items-center gap-2">
              🐹 <span>CapiRush</span>
            </h1>
          </div>
          <div className="flex gap-2">
            {isGameRunning && (
              <Button onClick={pauseResumeGame} variant="secondary" size="sm" data-testid="button-pause-resume">
                {isPaused ? <Play className="w-4 h-4 mr-1" /> : <Pause className="w-4 h-4 mr-1" />}
                {isPaused ? 'Resume' : 'Pause'}
              </Button>
            )}
            <Button onClick={resetGame} variant="destructive" size="sm" data-testid="button-reset">
              <RotateCcw className="w-4 h-4 mr-1" />
              Reset
            </Button>
          </div>
        </div>

        {/* Stats */}
        <div className="grid grid-cols-3 gap-4 mb-6">
          <Card className="p-4 bg-white/10 backdrop-blur-sm border-white/20">
            <div className="flex items-center gap-2">
              <Zap className="w-5 h-5 text-yellow-400" />
              <div>
                <p className="text-sm text-white/70">Current Score</p>
                <p className="text-xl font-bold text-white" data-testid="text-current-score">{currentScore}</p>
              </div>
            </div>
          </Card>
          <Card className="p-4 bg-white/10 backdrop-blur-sm border-white/20">
            <div className="flex items-center gap-2">
              <Trophy className="w-5 h-5 text-amber-400" />
              <div>
                <p className="text-sm text-white/70">High Score</p>
                <p className="text-xl font-bold text-white" data-testid="text-high-score">{gameStats.highScore}</p>
              </div>
            </div>
          </Card>
          <Card className="p-4 bg-white/10 backdrop-blur-sm border-white/20">
            <div className="flex items-center gap-2">
              <Heart className="w-5 h-5 text-red-400" />
              <div>
                <p className="text-sm text-white/70">Games Played</p>
                <p className="text-xl font-bold text-white" data-testid="text-games-played">{gameStats.gamesPlayed}</p>
              </div>
            </div>
          </Card>
        </div>
      </div>

      {/* Game Area */}
      <div className="max-w-6xl mx-auto flex flex-col items-center">
        <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6 border border-white/30">
          {!isGameRunning ? (
            <div className="text-center">
              <h2 className="text-2xl font-bold text-white mb-4">Ready for the Rush?</h2>
              <p className="text-white/80 mb-6">
                Help the capybara navigate through an endless runner adventure! Dodge obstacles and rack up points!
              </p>
              <Button onClick={startGame} size="lg" className="bg-teal-600 hover:bg-teal-700" data-testid="button-start-capirush">
                🐹 Start CapiRush
              </Button>
            </div>
          ) : (
            <div className="relative">
              {isLoading && (
                <div className="absolute inset-0 flex items-center justify-center bg-black/50 z-10 rounded-lg">
                  <div className="text-white text-center">
                    <div className="text-4xl mb-2">🎮</div>
                    <p className="mb-4">Loading CapiRush...</p>
                    <div className="w-64 bg-gray-700 rounded-full h-4">
                      <div 
                        className="bg-gradient-to-r from-teal-400 to-green-500 h-4 rounded-full transition-all"
                        style={{ width: `${loadingProgress}%` }}
                      ></div>
                    </div>
                    <p className="mt-2">{Math.round(loadingProgress)}%</p>
                  </div>
                </div>
              )}
              {isPaused && (
                <div className="absolute inset-0 flex items-center justify-center bg-black/70 z-10 rounded-lg">
                  <div className="text-white text-center">
                    <div className="text-6xl mb-4">⏸️</div>
                    <p className="text-2xl">Game Paused</p>
                    <p className="text-sm mt-2">Press Resume to continue</p>
                  </div>
                </div>
              )}
              <canvas 
                ref={canvasRef}
                width={800}
                height={500}
                className="border-2 border-teal-400/50 rounded-lg"
                style={{ width: '800px', height: '500px', background: '#000' }}
              />
            </div>
          )}
        </div>

        {/* Instructions */}
        <div className="mt-6 bg-white/10 backdrop-blur-sm rounded-lg p-4 border border-white/30">
          <h3 className="text-lg font-semibold text-white mb-2">How to Play:</h3>
          <div className="text-white/80 space-y-1">
            <p>• Use <kbd className="bg-white/20 px-2 py-1 rounded">WASD</kbd> or <kbd className="bg-white/20 px-2 py-1 rounded">Arrow Keys</kbd> to move</p>
            <p>• Dodge red cars and green balls</p>
            <p>• Score points by surviving and avoiding obstacles</p>
            <p>• Press <kbd className="bg-white/20 px-2 py-1 rounded">C</kbd> to change camera view</p>
          </div>
        </div>
      </div>
    </div>
  );
};

